import axios from "axios"
import Constants from "../Utilities/Constants";
import { FAIL_REQUEST, GET_CONTACT_LIST, DELETE_CONTACT, MAKE_REQUEST } from "./ActionType"

export const makeRequest=()=>{
    return{
        type:MAKE_REQUEST
    }
}
export const failRequest=(err)=>{
    return{
        type:FAIL_REQUEST,
        payload:err
    }
}
export const geContactList=(data)=>{
    return{
        type:GET_CONTACT_LIST,
        payload:data
    }
}

export const deleteContact=()=>{
    return{
        type:DELETE_CONTACT
    }
}

export const FetchContactList=()=>{
    return (dispatch)=>{
      dispatch(makeRequest());
      //setTimeout(() => {
        axios.get(Constants.API_URL_GET_ALL_CONTACTS).then(res=>{
            const contactList=res.data;
            dispatch(geContactList(contactList));
          }).catch(err=>{
            dispatch(failRequest(err.message))
          })
     // }, 2000);
     
    }
}

export const RemoveContact=(code)=>{
    return (dispatch)=>{
      dispatch(makeRequest());
      //setTimeout(() => {
        axios.delete(Constants.API_URL_DELETE_CONTACT_BY_ID + '/' + code).then(res=>{
            dispatch(deleteContact());
          }).catch(err=>{
            dispatch(failRequest(err.message))
          })
     // }, 2000);
     
    }
}
